INSERT INTO `notice_template_examples` (`id`, `name`, `description`, `template`) VALUES ('nt-cn25c0radq75sf2v1hf0', '通用告警/恢复模版', '通用告警/恢复模版', '{{- define \"Title\" -}}\n{{- if not .IsRecovered -}}\n【报警中】- 即时设计业务系统 🔥\n{{- else -}}\n【已恢复】- 即时设计业务系统 ✨\n{{- end -}}\n{{- end }}\n\n{{- define \"TitleColor\" -}}\n{{- if not .IsRecovered -}}\nred\n{{- else -}}\ngreen\n{{- end -}}\n{{- end }}\n\n{{ define \"Event\" -}}\n{{- if not .IsRecovered -}}\n**🤖 报警类型:** {{.RuleName}}\n**🫧 报警指纹:** {{.Fingerprint}}\n**📌 报警等级:** P{{.Severity}}\n**🖥 报警主机:** {{.Instance}}\n**🕘 开始时间:** {{.FirstTriggerTimeFormat}}\n**👤 值班人员:** {{.DutyUser}}\n**📝 报警事件:** {{.Annotations}}\n{{- else -}}\n**🤖 报警类型:** {{.RuleName}}\n**🫧 报警指纹:** {{.Fingerprint}}\n**📌 报警等级:** P{{.Severity}}\n**🖥 报警主机:** {{.Instance}}\n**🕘 开始时间:** {{.FirstTriggerTimeFormat}}\n**🕘 恢复时间:** {{.RecoverTimeFormat}}\n**👤 值班人员:** {{.DutyUser}}\n**📝 报警事件:** {{.Annotations}}\n{{- end -}}\n{{ end }}\n\n{{- define \"Footer\" -}}\n🧑‍💻 即时设计 - 运维团队\n{{- end }}');
